options.afterRender:
openNewTab = (url) => {
  window.open(url, '_blank');
}

playOrPause = (audio_url) => {
  player = document.getElementById('panel_preview_player');

  if (player) {
    if (player.src == audio_url) {
      if (player.paused) {
        player.play();
      } else {
        player.pause();
      }
    } else {
      player.src = audio_url;
      player.play();
    }
  } else {
    player = document.createElement('audio');
    player.setAttribute('id', 'panel_preview_player');
    player.setAttribute('src', audio_url);
    document.body.appendChild(player);
    player.play();
  }
}

